propValues:
.chakra-code:has(pre) {
  width: 100%;
  border-radius: 4px;
  font-family: monospace;
  font-size: 75%;
  line-height: 1.25;
  overflow: auto;
  overflow-wrap: normal;
  padding: 8px;
  word-break: normal;
  background: #263238;
  _dark {
    background: black;
  }
  color: white;
}

.chakra-text .chakra-code {
  color: red.600;
  _dark {
    color: red.400;
  }
}

a {
  color: blue.400;
   _dark {
    color: blue.500;
  }
  text-decoration: none;
}

li .chakra-code:not(:has(pre)) {
  color: red.600;
  _dark {
    color: red.400;
  }
}

p:has(strong):has(a) {
  width: 100%;
  display: flex;
  justify-content: center;
}

strong a {
  width: fit-content;
  display: flex;
  background-color: purple.600;
  border: none;
  color: white !important;
  padding: 8px;
  text-align: center;
  text-decoration: none;
  display: inline-block;

  cursor: pointer;
  border-radius: 6px;
}

strong a:link { text-decoration: none; }

strong a:visited { text-decoration: none; }

strong a:hover {
  text-decoration: none;
    background-color: purple.500;
  _dark{
      background-color: purple.500;
  }

}

strong a:active { text-decoration: none; }

/* Colors */
:root {
  --dynaboard-text-color: #333333;
  --dynaboard-link-color: #ff0000;
  --dynaboard-code-color: #f3f4f6;
  --dynaboard-background-color: #f8f8f8;
}

/* Body */
body {
  font-family:

, sans-serif;
  color: var(--dynaboard-text-color);
  background-color: var(--dynaboard-background-color);
  margin: 0;
  padding: 0;
}

/* Headings */
h1,
h2,
h3,
h4,
h5,
h6 {
  font-family:

, sans-serif;
  color: var(--dynaboard-text-color);
  font-weight: 600;
  line-height: 1.2;
  margin-top: 24px;
  margin-bottom: 16px;
}

h1 {
  font-size: 32px;
}

h2 {
  font-size: 24px;
}

h3 {
  font-size: 20px;
}

h4 {
  font-size: 18px;
}

h5 {
  font-size: 16px;
}

h6 {
  font-size: 14px;
}

/* Links */
a {
  color: var(--dynaboard-link-color);
  text-decoration: none;
  font-weight: 600;
}

a:hover {
  text-decoration: underline;
}

/* Lists */
ul {
  margin: 0;
  padding: 0;
  list-style-type: disc;
}

ol {
  margin: 0;
  padding: 0;
  list-style-type: decimal;
}

li {
  margin: 8px 0px 8px 24px;
}

/* Blockquotes */
blockquote {
  margin: 0;
  padding: 16px;
  background-color: var(--dynaboard-code-color);
  border-left: 4px solid var(--dynaboard-link-color);
  color: var(--dynaboard-text-color);
  font-style: italic;
}

/* Code */
code {
  background-color: var(--dynaboard-code-color);
  font-family:

, monospace;
  font-size: 14px;
  padding: 2px 4px;
}

pre {
  background-color: var(--dynaboard-code-color);
  font-family:

, monospace;
  font-size: 14px;
  padding: 16px;
  overflow-x: auto;
  border-radius: 4px;
}

/* Tables */
table {
  border-collapse: collapse;
  width: 100%;
}

td,
th {
  border: 1px solid #e5e5e5;
  padding: 8px;
  text-align: left;
}

th {
  font-weight: 600;
  background-color: var(--dynaboard-code-color);
  color: var(--dynaboard-text-color);
}

/* Images */
img {
  max-width: 100%;
  height: auto;
  display: block;
  margin: 24px auto;
}
